import React from 'react'
import './style.css'
import P1 from '../../Assets/person.jpg'

export default function About() {
    useLayoutEffect(() => {
        const ctx = gsap.context(() => {
            document.body.style.overflowY = "hidden"
            gsap.registerPlugin(ScrollTrigger);
            const timelineHandler1 = gsap.timeline({

                onComplete: () => {
                    const timelineHandler2 = gsap.timeline();
                    timelineHandler2.addLabel('secondtime');
                    timelineHandler2.add('second')
                    timelineHandler2.fromTo('.bird', { translateY: '0%' }, { translateY: '-190%', duration: 3, ease: 'power4.Out', zIndex: '9999' }, 'second')
                    timelineHandler2.fromTo(".right-space", { height: '100vh' }, { height: '0vh', duration: '3' }, 'second')
                    // timelineHandler2.fromTo('.logoname', { opacity: '0' }, { opacity: '0' });
                    // timelineHandler2.fromTo('.tophr', { opacity: '0' }, { opacity: '0' });
                    // timelineHandler2.fromTo('.bottomhr', { opacity: '0' }, { opacity: '0' });
                    timelineHandler2.to('.center', { clipPath: 'polygon(0% 0px, 100% 0%, 100% 100%, 0% 100%)', duration: 3, zIndex: 1 }, 'second-=0');
                    timelineHandler2.fromTo('.left', { opacity: "0" }, { opacity: "1" })
                    timelineHandler2.to('.miidleSection', { opacity: "1" }, 'second-=0')
                    timelineHandler2.to('#serviceWrap', { display: "flex", delay: 2 }, 'second-=0')
                    timelineHandler2.to('body', { overflowY: "scroll", delay: 3 }, 'second-=0')
                    timelineHandler2.play('secondtime');
                },
            });
            timelineHandler1.addLabel('onetime');
            timelineHandler1.add('first');
            timelineHandler1.fromTo('.logoname', { opacity: '1' }, { opacity: '1', duration: 1 });
            timelineHandler1.fromTo('.tophr', { translateY: '-100%' }, { translateY: '0', duration: 1 }, 'first');
            timelineHandler1.fromTo('.bottomhr', { translateY: '100%' }, { translateY: '0', duration: 1 }, 'first');
            timelineHandler1.fromTo('.logoname', { transform: 'rotate(0deg)' }, { transform: 'rotate(90deg)', delay: 1 }, 'first');
            timelineHandler1.play('onetime');

        },);
        return () => ctx.revert();
    });

    return (
        <div className='about'>
            <div id="mySidenav" class="sidenav">
                <p class="logo">Weather Prediction</p>
                <a href="/" class="icon-a">&nbsp;Dashboard</a>
                <a href="/" class="icon-a">&nbsp;&nbsp;About Us</a>
                <a href="/" class="icon-a"> &nbsp;&nbsp;Developer</a>
                <a href="/" class="icon-a">&nbsp;&nbsp;Weather Prediction</a>
            </div>
            <div className='about-main'>
                <div className='prashant'>
                    <div className='left-data'>
                        <h1>Prashant Shingade</h1>
                        <p>Hello , I'm Prashant Shingade, a MscIt Student from Mumbai Univercity.Talking about my education I have completed my
                            BSC in Information Technology in year 2021.
                            Currently, I'm learning Mscit. I have started to do some self - paced projects in data science
                            like Will it Rain. This project
                            is my first ever contribution to Data Science Community. Always curious to learn something new in the
                            field of Data Science. Beside's programming,
                            I enjoy playing games and listening to music.</p>
                    </div>
                    <div className='right-data'>
                        <img src={P1} />
                    </div>
                </div>
                <div className='sushant'>
                    <div className='left-sushant-data'>
                        <img src={P1} />
                    </div>
                    <div className='right-sushant-data'>
                        <h1>Sushant Waghmare</h1>
                        <p>Hello , I'm Prashant Shingade, a MscIt Student from Mumbai Univercity.Talking about my education I have completed my
                            BSC in Information Technology in year 2021.
                            Currently, I'm learning Mscit. I have started to do some self - paced projects in data science
                            like Will it Rain. This project
                            is my first ever contribution to Data Science Community. Always curious to learn something new in the
                            field of Data Science. Beside's programming,
                            I enjoy playing games and listening to music.</p>
                    </div>
                </div>
            </div>
        </div>
    )
}
